using UnityEngine;public class OutlineMgr : MonoBehaviour{	public enum ETech { NormalExpansion, PostProcess };	public ETech m_Tech = ETech.NormalExpansion;	ETech m_PrevTech;	Outline[] m_Outlines;	OutlineFilter m_OutlineFilter;	public string m_Layer = "Water";  // use your own layer name		void Start ()	{		m_Outlines = GameObject.FindObjectsOfType<Outline> ();		for (int i = 0; i < m_Outlines.Length; i++)			m_Outlines[i].Initialize ();		m_OutlineFilter = GameObject.FindObjectOfType<OutlineFilter> ();		OnTechChange ();	}	void Update ()	{		// when tech changed, do cleanup logic		if (m_PrevTech != m_Tech)			OnTechChange ();					for (int i = 0; i < m_Outlines.Length; i++)			m_Outlines[i].UpdateSelfParameters ();	}	void OnTechChange ()	{		m_PrevTech = m_Tech;		for (int i = 0; i < m_Outlines.Length; i++)			m_Outlines[i].DisableFx ();					if (m_Tech == ETech.PostProcess)			m_OutlineFilter.enabled = true;		else			m_OutlineFilter.enabled = false;	}}